     &lt;div xml:id="chap.I" type="chapter"&gt;&lt;/div&gt;
     &lt;div xml:id="chap.II" type="chapter"&gt;&lt;/div&gt;
     &lt;div xml:id="chap.III" type="chapter"&gt;&lt;/div&gt;
